INSERT INTO `book` VALUES ('34', '市进阶之道：一个散户的自我修养', '30', '李杰', '9', '9787113180508', '55.00', '55.00', '本书其核心内容分为三大部分，其分别对应着投资之道、价值之源和估值之谜。它们正是投资中最为关键和困难的部分、其既相互独立又具有内在联系，对它们是否具有系统和辩证的理解是一个投资人段位差别的体现。这其中，对于概率和赔率、认知偏差、投资陷阱等方面的内容将帮助初学者把握住投资的本质和学习的方向，而对不同生意形态和经营特征的论述、对高价值企业概念和投资策略的三个视角的介绍，以及对于市场定价机理的分析，将促使投资人进入更深层次的思考。', 'bookimg/0034.jpg', null, '0', '中国铁道出版社', '2014-04-01', '2020-06-01', '16开', '胶版纸', '平装-胶订', '否');
